import React, { useState, useEffect } from 'react';
// import { useLocation, useNavigate } from 'react-router-dom'; // React Router를 사용할 경우
import { GoogleOAuthProvider, GoogleLogin } from '@react-oauth/google';

const api = process.env.REACT_APP_BACKEND_URL;

const App = () => {
  const [currentTime, setCurrentTime] = useState(Math.floor(Date.now() / 1000));
  const [idToken, setIdToken] = useState(null);
  const [idTokenInfo1, setIdTokenInfo1] = useState(null);
  const [temp_acc, setTempAcc] = useState(null);
  const [aa_addr, setAaAddr] = useState(null);
  const [operation, setOperation] = useState('');
  const [accountInfo, setAccountInfo] = useState('');

  // toggle setting
  const [showToken, setShowToken] = useState(false);
  const toggleToken = () => { setShowToken(!showToken);};

  useEffect(() => {
    // 1초마다 시간을 업데이트
    const intervalId = setInterval(() => {
      setCurrentTime(Math.floor(Date.now() / 1000));
    }, 1000);

    // 컴포넌트가 언마운트될 때 인터벌을 클리어
    return () => clearInterval(intervalId);
  }, []);


  // Login handling
  const handleLoginSuccess = async (credentialResponse) => {
    console.log('Login Success:', credentialResponse);
    const token = credentialResponse.credential;
    setIdToken(token);

    try {
      const response = await fetch(api + '/auth/verify/idtoken/google', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ token: token })
      });

      const data = await response.json();
      console.log(data);

      if (data.status === 'success') {
        console.log(data.user)
        setIdTokenInfo1(data.user);
      } else {
        console.error('Token verification failed');
      }
    } catch (error) {
      console.error('Error verifying token:', error);
    }

    try {
      console.log()
      const response = await fetch(api + '/mitum/account/create', {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json'
        },
      });

      const data = await response.json();
      console.log(data);
      if (data.status === 'success') {
        console.log(data.temp_acc, data.aa_address);
        setTempAcc(data.temp_acc);
        setAaAddr(data.aa_address);
      } else {
        console.error('failed to crete new account');
      }
    } catch (error) {
      console.error(error);
    }
  };

  const handleLoginError = () => {
    console.error('Login Failed');
  };

  const handleTransfer = async() => {
    try {
      const response = await fetch(api + '/mitum/account/transfer', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ expired_time: idToken.exp })
      });

      const data = await response.json();
      console.log(data);

      if (data.status === 'success') {
        console.log(data.operation)
        setOperation(data.operation);
      } else {
        console.error('Token verification failed');
      }
    } catch (error) {
      console.error('Error verifying token:', error);
    }
  }

  const handleGetInfo = async() => {
    try {
      console.log()
      const response = await fetch(api + '/mitum/account/getinfo', {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json'
        },
      });

      const data = await response.json();
      console.log(data);
      if (data.status === 'success') {
        console.log(data.result);
        setAccountInfo(data.result);
      } else {
        console.error('failed to get account information');
      }
    } catch (error) {
      console.error(error);
    }
  }

  const verifyIdTokenByGoogle = async () => {
    if (!idToken) {
      console.error('No id token available');
      return;
    }

    try {
      const response = await fetch(api + '/auth/verify/idtoken/google', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ token: idToken })
      });

      const data = await response.json();
      if (data.status === 'success') {
        console.log(data.user)
        setIdTokenInfo1(data.user);
      } else {
        console.error('Token verification failed');
      }
    } catch (error) {
      console.error('Error verifying token:', error);
    }

  }

  return (
    <div>
      <div>
        <div>
          <h1>Account Abstraction with Google OAuth 2.0</h1>
          <p>Current UTC Time: {currentTime}</p>
          {idTokenInfo1 ? <p>{"Expired Time: " + idTokenInfo1.exp}</p> : <p></p>}
        </div>
        <div
          style={{
            width: "100%",
            textAlign: "center",
            borderBottom: "1px solid #aaa",
            lineHeight: "0.1em",
            margin: "10px 0 20px",
          }}
        >
        </div>
        <GoogleOAuthProvider clientId={process.env.REACT_APP_GOOGLE_CLIENT_ID}>
          <p>구글 로그인 하여 계정 생성 ⬇️</p>
          <GoogleLogin
            onSuccess={handleLoginSuccess}
            onError={handleLoginError}
            // useOneTap
            flow="auth-code"
            scope="openid profile email"
          />
          <br />
        </GoogleOAuthProvider>
      </div>
      <div>
        <h3>ID token</h3>
        {idToken ? (
          <div onClick={toggleToken} style={{ cursor: 'pointer', wordBreak: 'break-all' }}>
            <span style={{ color: 'black', textDecoration: 'none', border: 'none', background: 'none' }}>
              {!showToken ? idToken.slice(0, 120) + "..." : idToken}
            </span>
          </div>
        ) : (
          <div></div>
        )}
        <h3>User Info from ID token</h3>
        {idTokenInfo1 ? <pre>{JSON.stringify(idTokenInfo1, null, 2)}</pre> : <p>null</p>}
      </div>
      <div>
        <h3>Temp Account, AA Account</h3>
        <div>
          <p>Temp Account:</p>
          <pre>{JSON.stringify(temp_acc, null, 2)}</pre>
          <p>{`AA Account: ${aa_addr}`}</p>
        </div>
      </div>
      <div>
        <button onClick={handleGetInfo}>
          Get information of AA account
        </button>
        <h3>AA account information</h3>
        {accountInfo ? <pre>{JSON.stringify(accountInfo, null, 2)}</pre> : <p>null</p>}
        <button onClick={handleTransfer}>
          Transfer using AA account
        </button>
        <h3>Operation</h3>
        {operation ? <pre>{JSON.stringify(operation, null, 2)}</pre> : <p>null</p>}
      </div>
    </div>
  );
};

export default App;
