import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom'; // React Router를 사용할 경우

const api = process.env.REACT_APP_BACKEND_URL;

const App = () => {
  const [currentTime, setCurrentTime] = useState(Math.floor(Date.now() / 1000));
  const [idToken, setIdToken] = useState(null);
  const [authCode, setAuthCode] = useState(null);
  const [idTokenInfo1, setIdTokenInfo1] = useState(null);
  const [refreshToken, setRefreshToken] = useState('');
  const [isExpired, setIsExpired] = useState(false);
  // account related
  const [temp_acc, setTempAcc] = useState(null);
  const [aa_addr, setAaAddr] = useState(null);
  const [operation, setOperation] = useState('');
  const [accountInfo, setAccountInfo] = useState('');

  // toggle setting
  const [showToken, setShowToken] = useState(false);
  const toggleToken = () => { setShowToken(!showToken);};

  const location = useLocation();
  const navigate = useNavigate(); // useNavigate 훅 사용



  useEffect(() => {
    // URL 쿼리 파라미터에서 Authorization Code를 추출합니다.
    const query = new URLSearchParams(location.search);
    const code = query.get('auth_code'); // URL에서 auth_code를 추출

    if (code) {
      setAuthCode(code);
      console.log('Authorization Code:', code);
      // 인증 코드 처리 후 리디렉션
      navigate('/', { replace: true }); // React Router를 통한 리디렉션
    }

    const timeIntervalId = setInterval(() => {
      setCurrentTime(Math.floor(Date.now() / 1000));
    }, 1000);

    const isTokenExpired = () => {
      if (idTokenInfo1) {
        setIsExpired(idTokenInfo1.exp <= currentTime);
      }
    };

    return () => {
      clearInterval(timeIntervalId);
      clearInterval(isTokenExpired);
    };

  }, [location.search, navigate]);

  const getAuthCode = (clientId) => {
    const redirectUri = 'http://localhost:8080/callback';
    const responseType = 'code'; // Authorization code flow
    const scope = 'openid profile email'; // 요청할 범위
    const accessType = 'offline'; // 리프레시 토큰을 포함하기 위한 옵션

    const authUrl = `https://accounts.google.com/o/oauth2/auth?client_id=${clientId}&redirect_uri=${redirectUri}&response_type=${responseType}&scope=${scope}&access_type=${accessType}`;

    window.location.href = authUrl; // 구글 인증 페이지로 리디렉션
  };

  const issueToken = async () => {
    console.log("Issuing token using code:", authCode);

    try {
      // Token 발급 요청
      const tokenRes = await fetch(api + '/auth/tokens', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ code: authCode })
      });

      const { status, idToken, accessToken, refreshToken } = await tokenRes.json();

      if (status !== "success") throw new Error("Failed to issue tokens");

      console.log(`Tokens issued - Access: ${accessToken}, Refresh: ${refreshToken}`);
      setRefreshToken(refreshToken);
      setIdToken(idToken);

      await verifyIdToken(idToken);
      await createAccount();

    } catch (error) {
      console.error("Error in token issuance flow:", error);
    }
  };

  const verifyIdToken = async (idToken) => {
    try {
      const verifyRes = await fetch(api + '/auth/verify/idtoken/google', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ token: idToken })
      });

      const { status, user } = await verifyRes.json();
      if (status !== 'success') throw new Error("Token verification failed");

      console.log("Verified user:", user);
      setIdTokenInfo1(user);

    } catch (error) {
      console.error("Error verifying token:", error);
    }
  };

  const createAccount = async () => {
    try {
      const accountRes = await fetch(api + '/mitum/account/create', {
        method: 'GET',
        headers: { 'Content-Type': 'application/json' }
      });

      const { status, temp_acc, aa_address } = await accountRes.json();
      if (status !== 'success') throw new Error("Failed to create new account");

      console.log("Account created - TempAcc:", temp_acc, "AA Address:", aa_address);
      setTempAcc(temp_acc);
      setAaAddr(aa_address);

    } catch (error) {
      console.error("Error creating account:", error);
    }
  };

  const handleTransfer = async() => {
    try {
      const response = await fetch(api + '/mitum/account/transfer', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ expired_time: idToken.exp })
      });

      const data = await response.json();
      console.log(data);

      if (data.status === 'success') {
        console.log(data.operation)
        setOperation(data.operation);
      } else {
        console.error('Token verification failed');
      }
    } catch (error) {
      console.error('Error verifying token:', error);
    }
  }

  const handleGetInfo = async() => {
    try {
      console.log()
      const response = await fetch(api + '/mitum/account/getinfo', {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json'
        },
      });

      const data = await response.json();
      console.log(data);
      if (data.status === 'success') {
        console.log(data.result);
        setAccountInfo(data.result.data[0]);
      } else {
        console.error('failed to get account information');
      }
    } catch (error) {
      console.error(error);
    }
  }

  return (
    <div>
      <div>
        <div>
          <h1>Account Abstraction with Google OAuth 2.0</h1>
          <p>Current UTC Time: {currentTime}</p>
          {idTokenInfo1 ? <p>{"Expired Time: " + idTokenInfo1.exp}</p> : <p></p>}
        </div>
        <div
          style={{
            width: "100%",
            textAlign: "center",
            borderBottom: "1px solid #aaa",
            lineHeight: "0.1em",
            margin: "10px 0 20px",
          }}
        >
        </div>
        <div>
          <p>구글 로그인 하여 계정 생성 ⬇️</p>
          { authCode ? (<div></div>) 
            : (<button onClick={() => getAuthCode(process.env.REACT_APP_GOOGLE_CLIENT_ID)}>Login with Google</button>)
          }
          <button onClick={() => issueToken()}>
            계정 생성하기
          </button>
          <br />
        </div>
      </div>
      <div>
        <h3>ID token & Refresh token</h3>
        {idToken ? (
          <div onClick={toggleToken} style={{ cursor: 'pointer', wordBreak: 'break-all' }}>
            <span style={{ color: 'black', textDecoration: 'none', border: 'none', background: 'none' }}>
              {!showToken ? idToken.slice(0, 120) + "..." : idToken}
            </span>
          </div>
        ) : (
          <div></div>
        )}
        <div>   </div>
        {refreshToken ? (
          <div onClick={toggleToken} style={{ cursor: 'pointer', wordBreak: 'break-all' }}>
            <span style={{ color: 'black', textDecoration: 'none', border: 'none', background: 'none' }}>
              {!showToken ? refreshToken.slice(0, 120) + "..." : refreshToken}
            </span>
          </div>
        ) : (
          <div></div>
        )}
        <h3>User Info from ID token</h3>
        {idTokenInfo1 ? <pre>{JSON.stringify(idTokenInfo1, null, 2)}</pre> : <p>null</p>}
      </div>
      <div>
        <h3>Temp Account, AA Account</h3>
        <div>
          <p>Temp Account:</p>
          <pre>{JSON.stringify(temp_acc, null, 2)}</pre>
          <p>{`AA Account: ${aa_addr}`}</p>
        </div>
      </div>
      <div>
        <button onClick={handleGetInfo}>
          Get Balance of AA account
        </button>
        {accountInfo ? <pre>{JSON.stringify(accountInfo, null, 2)}</pre> : <p>null</p>}
      </div>
      <div>
        <button onClick={handleTransfer}>
          Transfer using AA account
        </button>
        {operation ? (
          <div>
            <p>Operation</p>
            <pre>{JSON.stringify(operation, null, 2)}</pre>
          </div>
        ) : (
          <div></div>
        )}
      </div>
    </div>
  );
};

export default App;
